def solve():
    data = sys.stdin.read().split()
    if not data:
        return
    nums = list(map(int, data))
    t = nums[0]
    queries = nums[1:]
    # Ensure we only process t queries
    if len(queries) > t:
        queries = queries[:t]

    for x in queries:
        if x >= 12 or x in (0, 3, 6, 7, 9, 10):
            print("YES")
        else:
            print("NO")

if __name__ == "__main__":
    solve()
