class MedianSum:
    def __init__(self):
        self.elements = []

    def add(self, x):
        # Insert x in sorted order
        bisect.insort(self.elements, x)

    def delete(self, x):
        # Find and remove x
        idx = bisect.bisect_left(self.elements, x)
        if idx < len(self.elements) and self.elements[idx] == x:
            self.elements.pop(idx)

    def sum_medians(self):
        if len(self.elements) < 5:
            return 0

        # Group consecutive elements into groups of 5
        total_sum = 0
        for i in range(0, len(self.elements), 5):
            group = self.elements[i:i+5]
            if len(group) == 5:
                # Median is the 3rd element (index 2) in sorted group
                median = group[2]
                # According to formula: sum += median % (i+1)
                total_sum += median % (i + 1 + 1)  # Adding 1 as per problem's 1-based indexing

        return total_sum

def main():
    n = int(input())
    median_sum = MedianSum()

    for _ in range(n):
        operation = input().strip().split()

        if operation[0] == "add":
            x = int(operation[1])
            median_sum.add(x)
        elif operation[0] == "del":
            x = int(operation[1])
            median_sum.delete(x)
        elif operation[0] == "sum":
            result = median_sum.sum_medians()
            print(result)

if __name__ == "__main__":
    main()"}}

